import{_ as e,c as a,o as s,a4 as t}from"./chunks/framework.CSOa_3UV.js";const f=JSON.parse(`{"title":"Cours d'Automatique","description":"","frontmatter":{},"headers":[],"relativePath":"content/course/00_introduction/01_aav/index.md","filePath":"content/course/00_introduction/01_aav/index.md"}`),i={name:"content/course/00_introduction/01_aav/index.md"},r=t('<h1 id="cours-d-automatique" tabindex="-1">Cours d&#39;Automatique <a class="header-anchor" href="#cours-d-automatique" aria-label="Permalink to &quot;Cours d&#39;Automatique&quot;">​</a></h1><h2 id="liste-des-acquis-d-apprentissage-vises" tabindex="-1">Liste des Acquis d&#39;Apprentissage Visés <a class="header-anchor" href="#liste-des-acquis-d-apprentissage-vises" aria-label="Permalink to &quot;Liste des Acquis d&#39;Apprentissage Visés&quot;">​</a></h2><h3 id="aav1-–-modelisation-analyse-et-identification-des-slit" tabindex="-1">AAV1 – Modélisation, analyse et identification des SLIT <a class="header-anchor" href="#aav1-–-modelisation-analyse-et-identification-des-slit" aria-label="Permalink to &quot;AAV1 – Modélisation, analyse et identification des SLIT&quot;">​</a></h3><p>À la fin du semestre, les étudiants seront capables de :</p><ul><li>[C1] Modéliser un système physique linéaire et invariant dans le temps (SLIT), à une entrée et une sortie (SISO), sous forme de fonction de transfert à partir d’une équation différentielle.</li><li>[C2] Analyser la stabilité (pôles), le régime permanent et le comportement dynamique (temps de réponse, dépassement) des systèmes de 1er ou 2nd ordre.</li><li>[C3] Identifier un système de 1er ou 2nd ordre à partir de mesures (réponse indicielle, impulsionnelle, régime sinusoïdal permanent (harmonique)).</li></ul><h3 id="aav2-–-analyse-des-systemes-en-boucle-fermee" tabindex="-1">AAV2 – Analyse des systèmes en boucle fermée <a class="header-anchor" href="#aav2-–-analyse-des-systemes-en-boucle-fermee" aria-label="Permalink to &quot;AAV2 – Analyse des systèmes en boucle fermée&quot;">​</a></h3><p>À la fin du semestre, les étudiants seront capables de :</p><ul><li>[C1] Déterminer la fonction de transfert d’un système asservi en boucle fermée et distinguer les contributions de la commande et de la perturbation sur la sortie.</li><li>[C2] Utiliser un diagramme de Black pour prédire les performances d&#39;un système en boucle fermée : stabilité (marges de phase et de gain), précision (erreur finale), comportement dynamique (résonance).</li></ul><h3 id="aav3-–-synthese-frequentielle-de-correcteurs-lineaires" tabindex="-1">AAV3 – Synthèse fréquentielle de correcteurs linéaires <a class="header-anchor" href="#aav3-–-synthese-frequentielle-de-correcteurs-lineaires" aria-label="Permalink to &quot;AAV3 – Synthèse fréquentielle de correcteurs linéaires&quot;">​</a></h3><p>À la fin du semestre, les étudiants seront capables de :</p><ul><li>[C1] Concevoir un correcteur analogique (P, PI, PID ou avance de phase) en respectant un cahier des charges portant sur la stabilité, la précision et la dynamique du système.</li><li>[C2] Justifier leurs choix à l’aide de représentations fréquentielles (diagrammes de Bode ou de Black) et des dilemmes usuels (stabilité/rapidité, précision/bande passante).</li></ul><h3 id="aav4-–-implantation-et-validation-sur-systeme-reel" tabindex="-1">AAV4 – Implantation et validation sur système réel <a class="header-anchor" href="#aav4-–-implantation-et-validation-sur-systeme-reel" aria-label="Permalink to &quot;AAV4 – Implantation et validation sur système réel&quot;">​</a></h3><ul><li>[C1] Implémenter sur microcontrôleur un correcteur linéaire (P, PI ou PID) en utilisant une discrétisation numérique adaptée (ex. méthode d’Euler).</li><li>[C2] Utiliser un oscilloscope ou un système de mesure numérique pour caractériser le comportement d&#39;un système.</li><li>[C3] Valider par simulation et expérimentalement les performances d&#39;une stratégie de commande : stabilité, précision, rapidité, robustesse.</li></ul>',13),n=[r];function l(o,d,u,c,m,p){return s(),a("div",null,n)}const q=e(i,[["render",l]]);export{f as __pageData,q as default};
