import{_ as i,c as a,o as e,a4 as s}from"./chunks/framework.CSOa_3UV.js";const m=JSON.parse('{"title":"Installation des outils","description":"","frontmatter":{},"headers":[],"relativePath":"content/tutorial/01_installation.md","filePath":"content/tutorial/01_installation.md"}'),t={name:"content/tutorial/01_installation.md"},n=s('<h1 id="installation-des-outils" tabindex="-1">Installation des outils <a class="header-anchor" href="#installation-des-outils" aria-label="Permalink to &quot;Installation des outils&quot;">​</a></h1><h2 id="installation-de-miniconda" tabindex="-1">Installation de Miniconda <a class="header-anchor" href="#installation-de-miniconda" aria-label="Permalink to &quot;Installation de Miniconda&quot;">​</a></h2><h3 id="presentation-de-miniconda" tabindex="-1">Présentation de Miniconda <a class="header-anchor" href="#presentation-de-miniconda" aria-label="Permalink to &quot;Présentation de Miniconda&quot;">​</a></h3><p>Miniconda est une distribution légère de Conda, un gestionnaire de paquets et d&#39;environnements pour les langages de programmation, notamment Python. Miniconda comprend Conda, Python et ses dépendances essentielles, permettant ainsi aux utilisateurs de créer des environnements isolés pour différents projets. Cela garantit que les bibliothèques et les dépendances d&#39;un projet n&#39;interfèrent pas avec celles d&#39;un autre.</p><p><strong>Avantages de Miniconda :</strong></p><ul><li>Installation rapide et légère.</li><li>Permet de gérer facilement les dépendances des projets.</li><li>Crée des environnements isolés.</li><li>Compatible avec de nombreuses plateformes (Windows, macOS, Linux).</li></ul><h3 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h3><details class="details custom-block"><summary>Windows</summary><ol><li><p><strong>Télécharger l&#39;installateur :</strong></p><ul><li>Rendez-vous sur la page de <a href="https://docs.conda.io/en/latest/miniconda.html" target="_blank" rel="noreferrer">téléchargement de Miniconda</a>.</li><li>Choisissez l&#39;installateur pour Windows (64-bit ou 32-bit selon votre système).</li></ul></li><li><p><strong>Lancer l&#39;installateur :</strong></p><ul><li>Double-cliquez sur le fichier téléchargé pour lancer l&#39;installation.</li><li>Suivez les instructions à l&#39;écran. Il est recommandé de choisir l&#39;option &quot;Add Miniconda to my PATH environment variable&quot;.</li></ul></li><li><p><strong>Vérifier l&#39;installation :</strong></p><ul><li>Ouvrez le programme Anaconda Prompt.</li><li>Tapez <code>conda --version</code> pour vérifier que Conda est bien installé.</li></ul></li></ol></details><details class="details custom-block"><summary>macOS</summary><ol><li><p><strong>Télécharger l&#39;installateur :</strong></p><ul><li>Rendez-vous sur la page de <a href="https://docs.conda.io/en/latest/miniconda.html" target="_blank" rel="noreferrer">téléchargement de Miniconda</a>.</li><li>Choisissez l&#39;installateur pour macOS (64-bit).</li></ul></li><li><p><strong>Lancer l&#39;installateur :</strong></p><ul><li>Ouvrez un terminal.</li><li>Naviguez jusqu&#39;au répertoire où l&#39;installateur a été téléchargé.</li><li>Tapez <code>bash Miniconda3-latest-MacOSX-x86_64.sh</code> et suivez les instructions à l&#39;écran.</li></ul></li><li><p><strong>Vérifier l&#39;installation :</strong></p><ul><li>Ouvrez un nouveau terminal.</li><li>Tapez <code>conda --version</code> pour vérifier que Conda est bien installé.</li></ul></li></ol></details><details class="details custom-block"><summary>Linux</summary><ol><li><p><strong>Télécharger l&#39;installateur :</strong></p><ul><li>Rendez-vous sur la page de <a href="https://docs.conda.io/en/latest/miniconda.html" target="_blank" rel="noreferrer">téléchargement de Miniconda</a>.</li><li>Choisissez l&#39;installateur pour Linux (64-bit ou 32-bit selon votre système).</li></ul></li><li><p><strong>Lancer l&#39;installateur :</strong></p><ul><li>Ouvrez un terminal.</li><li>Naviguez jusqu&#39;au répertoire où l&#39;installateur a été téléchargé.</li><li>Tapez <code>bash Miniconda3-latest-Linux-x86_64.sh</code> (ou la version 32-bit si applicable) et suivez les instructions à l&#39;écran.</li></ul></li><li><p><strong>Vérifier l&#39;installation :</strong></p><ul><li>Ouvrez un nouveau terminal.</li><li>Tapez <code>conda --version</code> pour vérifier que Conda est bien installé.</li></ul></li></ol></details><h2 id="creation-d-un-environnement-dedie" tabindex="-1">Création d&#39;un environnement dédié <a class="header-anchor" href="#creation-d-un-environnement-dedie" aria-label="Permalink to &quot;Création d&#39;un environnement dédié&quot;">​</a></h2><p>Pour illustrer le contenu de ce cours, nous allons utiliser un environnement python 3.11 et des librairies Python spécifiques.</p><ol><li><p><strong>Créer un nouvel environnement :</strong></p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> create</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --name</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> enib_asa</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> python=</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">3.11</span></span></code></pre></div></li><li><p><strong>Activer l&#39;environnement :</strong></p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> activate</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> enib_asa</span></span></code></pre></div></li><li><p><strong>Installer des paquets avec conda :</strong></p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -c</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> conda-forge</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> plotly</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> control</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> jupyterlab</span></span></code></pre></div></li><li><p><strong>Installer control-plotly avec pip :</strong></p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">pip</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> control-plotly</span></span></code></pre></div></li></ol><h2 id="utilisation-de-jupyter-lab" tabindex="-1">Utilisation de Jupyter Lab <a class="header-anchor" href="#utilisation-de-jupyter-lab" aria-label="Permalink to &quot;Utilisation de Jupyter Lab&quot;">​</a></h2><p>Dans votre terminal, lancez la commande</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">jupyter</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> lab</span></span></code></pre></div>',16),l=[n];function o(r,d,p,c,u,h){return e(),a("div",null,l)}const k=i(t,[["render",o]]);export{m as __pageData,k as default};
